/**
 * @title Proof of Existence
 * @author 0x7efC9d38581c7Cb8372325c349Ec5095D027B3Dd
 * @dev Notarization of documents via Blockchain
 * @custom:dev-run-script ./scripts/deploy_with_web3(PoE).ts
 */
contract ProofOfExistence {

    struct Proof {
        address owner;
        string tag;
        uint timestamp;
    }
 /// @dev The contract state
    mapping (bytes32 => Proof) private proofs;

    event Notarized(bytes32 hash, Proof proof);

    // Helper functions //

    /// @dev Public helper function to get a document's SHA256 hash
    function encode(string memory document) public pure returns (bytes32 hash) {
        return sha256(abi.encode(document));
    }

    function currentTimestamp() public view returns (uint timestamp) {
        return block.timestamp;
    }

    // Notarization functions //

    /// @dev Calculate and store the proof for a document in the contract state
    function notarize(bytes32 hash, string calldata tag) public returns (Proof memory) {
        require(proofs[hash].timestamp == 0 , "Document already notarized.");
        proofs[hash] = Proof(msg.sender, tag, currentTimestamp());
        emit Notarized(hash, proofs[hash]);
        return proofs[hash];
    }

    /// @dev Calculate and store the proof for a document in the contract state
    function upload(string calldata document, string calldata tag) external {
        notarize(encode(document), tag);
    }

    /// @dev Calculate and store the proof for a document in the contract state
    function test(string calldata document, string calldata tag) external returns (Proof memory) {
        return notarize(encode(document), tag); // return works only if called by contracts, not externally
    }

    // Verification functions //

    /// @dev Check if a hash has been notarized
    function verify(bytes32 hash) public view returns (bool) {
        return proofs[hash].timestamp != 0;
    }

    /// @dev Check if a hash has been notarized
    function check(string calldata document) external view returns (bool) {
        return verify(encode(document));
    }

    /// @dev Get the notarized Proof
    function getProof(bytes32 hash) external view returns (Proof memory) {
        return proofs[hash];
    }
}

